INSERT INTO `lg_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('323', '34', 'LG000323', '对开盒酒具三件套装', '+', '120', '58', '', '1000', '0.000', '69.60', '58.00', '0.00', '0', '0', '1', '酒具组合、酒具礼品、尚为酒具、酒具组盒', '', '<p><img alt=\"\" width=\"675\" height=\"204\" src=\"/images/upload/Image/1357550054(1).jpg\" /></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style=\"font-size: medium\">产品介绍</span></strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<div class=\"bodyzone\">\r\n<p>【品 名】对开盒酒具三件套装<br />\r\n【型 号】SW-WG01<br />\r\n【品 牌】SUNWAY</p>\r\n<p>本套装产品包括：</p>\r\n<p>海马开瓶器、真空保鲜瓶塞、亚克力倒酒器</p>\r\n<p>包装新颖独特、典雅高贵、美观时尚,是您送礼、馈赠的首选佳品。</p>\r\n<p>包装规格：22 x 12 x 5.0 (cm)</p>\r\n</div>\r\n<div class=\"bzone\">\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium\">品牌介绍</span></p>\r\n<p>&nbsp;</p>\r\n<div style=\"text-indent: 13.5pt\"><span style=\"font-size: 9pt\"><font color=\"#000000\"><span style=\"font-size: 9pt\">深圳市尚为酒具有限公司是一家专业从事酒具产品研发、生产与销售的现代化企业。拥有自己完善的产业链，包括模具车间、五金塑胶加工车间、装配车间、研发中心以及销售和售后团队。历经数年发展，尚为已发展成为中国最专业的酒具制造商之一。与此同时尚为的海外市场也一直保持着骄人业绩，营销网络遍布全球六十多个国家及地区。 </span></font></span></div>\r\n<div><span style=\"font-size: 9pt\">作为中国酒具行业的第一品牌，尚为一直在技术研发、产品创新等方面引领这一行业的发展。拥有多项国内及国际专利，从中国制造到中国创造，尚为让中国创造迈出国门，走向世界，并以专业品质赢得了全球消费者的认可与信赖。所有产品均已通过CE、ROHS、FDA、LFGB等国际相关产品质量认证以及符合相关的国内质量检测标准。而卓越的品质也正是尚为酒具畅销海内外的坚实后盾，尚为公司是国内首家取得ISO9001质量管理体系认证的酒具制造企业。</span></div>\r\n<div style=\"text-indent: 13.5pt\"><span style=\"font-size: 9pt\">尚为作为最早开拓国内市场的酒具品牌，市场占有率在同类品牌中独占鳌头，是国内酒具行业市场公认的最具影响力的专业酒具品牌。放眼未来，已步入快速发展阶段的尚为，立足发挥现有优势，加大创新力度，力争发展成为世界级的专业酒具制造商，努力向&ldquo;成为受人尊敬和最具竞争力的专业酒具行业领导企业&rdquo;的宏伟愿景迈进。</span></div>\r\n<div style=\"text-indent: 13.5pt\">&nbsp;</div>\r\n<div style=\"text-indent: 13.5pt\">&nbsp;</div>\r\n<div style=\"text-indent: 13.5pt\">&nbsp;</div>\r\n<div style=\"text-indent: 13.5pt\"><span style=\"font-size: 9pt\"><strong><font size=\"3\">发展历程：</font></strong></span></div>\r\n<div style=\"text-indent: 13.5pt\">&nbsp;</div>\r\n<span style=\"font-size: 9pt\">\r\n<div style=\"text-indent: -18pt; margin: 0cm 0cm 0pt 36pt\"><font size=\"2\"><span>&bull;<span style=\"font: 7pt \'Times New Roman\'\"> </span></span>2003年<span> </span>国际知名设计师ERIC JIANG 研发全球第一款电动开瓶器 </font></div>\r\n<div style=\"text-indent: -18pt; margin: 0cm 0cm 0pt 36pt\"><font size=\"2\"><span>&bull;<span style=\"font: 7pt \'Times New Roman\'\"> </span></span>2006年 ERIC JIANG 创立SUNWAY品牌，开始专注于酒具设计制造 </font></div>\r\n<div style=\"text-indent: -18pt; margin: 0cm 0cm 0pt 36pt\"><font size=\"2\"><span>&bull;<span style=\"font: 7pt \'Times New Roman\'\"> </span></span>2007年 全面进入欧美市场，并设立法国代表处 </font></div>\r\n<div style=\"text-indent: -18pt; margin: 0cm 0cm 0pt 36pt\"><font size=\"2\"><span>&bull;<span style=\"font: 7pt \'Times New Roman\'\"> </span></span>2008年 开始进入国内市场， </font></div>\r\n<div style=\"text-indent: -18pt; margin: 0cm 0cm 0pt 36pt\"><font size=\"2\"><span>&bull;<span style=\"font: 7pt \'Times New Roman\'\"> </span></span>2011年 进驻深圳国际葡萄酒城，设实体形象展厅 </font></div>\r\n<div style=\"text-indent: -18pt; margin: 0cm 0cm 0pt 36pt\"><font size=\"2\"><span>&bull;<span style=\"font: 7pt \'Times New Roman\'\"> <font size=\"2\">2012 年 全球最新款第四代新理念（UNIQUE DECANTER） 快速醒酒器全球首发</font></span></span></font></div>\r\n<div style=\"text-indent: -18pt; margin: 0cm 0cm 0pt 36pt\"><font size=\"2\"><span>&bull;<span style=\"font: 7pt \'Times New Roman\'\"> </span></span>2012年 成立北京代表处</font></div>\r\n<div style=\"text-indent: -18pt; margin: 0cm 0cm 0pt 36pt\"><font size=\"2\"><span style=\"font-size: 10.5pt\"><span><font size=\"2\">&bull;</font> </span>2012年 国内首家通过ISO质量管理体系认证的酒具制造企业</span></font></div>\r\n<div style=\"text-indent: 13.5pt\">&nbsp;</div>\r\n</span>\r\n<p>&nbsp;</p>\r\n<div id=\"banzhu\" class=\"banzhu\">&nbsp;</div>\r\n</div>', 'images/201301/thumb_img/323_thumb_G_1357604407669.jpg', 'images/201301/goods_img/323_G_1357604407281.jpg', 'images/201301/source_img/323_G_1357604407508.jpg', '1', '', '1', '1', '0', '0', '1357604407', '100', '0', '1', '0', '1', '0', '0', '1364877107', '7', '', '-1', '-1', '0', '');
