INSERT INTO `lg_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('441', '2', 'LG000441', '52° 西凤 凤香经典 30年大师级 500ml ', '+', '17', '19', '', '1', '0.000', '0.00', '0.00', '0.00', '0', '0', '1', '', '', '<p>&nbsp;<span style=\"color: rgb(119, 119, 119); font-size: 9pt; line-height: 16.5pt; text-indent: 18pt; \">西凤酒以当地特产高粱为原料，用大麦、豌豆制曲。工艺采用续渣发酵法，发酵窖分为明窖与暗窖两种。工艺流程分为立窖、破窖、顶窖、圆窖、插窖和挑窖等工序，自有一套操作方法。蒸馏得酒后，再经3年以上的贮存，然后进行精心勾兑方出厂。</span></p>\r\n<div style=\"line-height: 16.5pt; text-indent: 18pt; \" align=\"left\"><span style=\"COLOR: #777777; FONT-SIZE: 9pt\">西凤酒无色清亮透明，醇香芬芳，清而不淡，浓而不艳，集清香、浓香之优点融于一体，幽雅、诸味谐调，回味舒畅，风格独特。被誉为&ldquo;酸、甜、苦、辣、香五味俱全而各不出头&rdquo;。即酸而不涩，苦而不黏，香不刺鼻，辣不呛喉，饮后回甘、味久而弥芳之妙。属凤香型大曲酒，被人们赞为它是&ldquo;凤型&rdquo;白酒的典型代表。西凤酒为适应各地不同消费者的需要推出33度、38度、39度、42度、45度、46度、48度、50度、52度、55度、65度等多种度数。</span></div>\r\n<div style=\"line-height: 16.5pt; text-indent: 18pt; \" align=\"left\"><span style=\"COLOR: #777777; FONT-SIZE: 9pt\">更加大放异彩。1956年，国家投资在柳林镇建起了&ldquo;陕西省西凤酒厂&rdquo;，从此，西凤酒迅速发展，生产规模不断扩大，产量日趋增长，品质风格更加醇馥突出。在1952年、1963年和1984年的第一、二、四届全国评酒会上，西凤酒三次被评为国家名酒，两次荣获国家金质奖章。1984年，在轻工业部酒类质量大赛中，西凤酒又获得金杯奖。</span></div>\r\n<div style=\"line-height: 16.5pt; \" align=\"left\"><span style=\"COLOR: #777777; FONT-SIZE: 9pt\">　&nbsp;西凤酒具有&ldquo;凤型&rdquo;酒的独特风格。它清而不淡，浓而不艳，酸、甜、苦、辣、香，诸味谐调，又不出头。它把清香型和浓香型二者之优点融为一体，香与味、头与尾和调一致，属于复合香型的大曲白酒。西凤酒的特点是：酒液无色，清澈透明，清芳甘润、细致，入口甜润、醇厚、丰满，有水果香，尾净味长，为喜饮烈性酒者所钟爱。</span></div>\r\n<div style=\"line-height: 16.5pt; \" align=\"left\"><img src=\"/images/upload/Image/02(3).png\" width=\"675\" height=\"180\" alt=\"\" /></div>\r\n<div style=\"line-height: 16.5pt; \" align=\"left\"><img src=\"/images/upload/Image/baijiulie.jpg\" width=\"675\" height=\"2540\" alt=\"\" /></div>', 'images/201303/thumb_img/441_thumb_G_1363027152754.jpg', 'images/201303/goods_img/441_G_1363027152280.jpg', 'images/201303/source_img/441_G_1363027152385.png', '1', '', '1', '1', '0', '0', '1363027152', '100', '0', '0', '0', '1', '0', '0', '1363755008', '1', '', '-1', '-1', '0', '');
