INSERT INTO `lg_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('444', '2', 'LG000444', '52° 西凤 一品酒（精酿） 500ml', '+', '30', '19', '', '1', '0.000', '0.00', '0.00', '0.00', '0', '0', '1', '', '', '<p>&nbsp;西凤酒以当地特产高粱为原料，用大麦、豌豆制曲。工艺采用续渣发酵法，发酵窖分为明窖与暗窖两种。工艺流程分为立窖、破窖、顶窖、圆窖、插窖和挑窖等工序，自有一套操作方法。蒸馏得酒后，再经3年以上的贮存，然后进行精心勾兑方出厂。</p>\r\n<div>\r\n<p>西凤酒无色清亮透明，醇香芬芳，清而不淡，浓而不艳，集清香、浓香之优点融于一体，幽雅、诸味谐调，回味舒畅，风格独特。被誉为&ldquo;酸、甜、苦、辣、香五味俱全而各不出头&rdquo;。即酸而不涩，苦而不黏，香不刺鼻，辣不呛喉，饮后回甘、味久而弥芳之妙。属凤香型大曲酒，被人们赞为它是&ldquo;凤型&rdquo;白酒的典型代表。西凤酒为适应各地不同消费者的需要推出33度、38度、39度、42度、45度、46度、48度、50度、52度、55度、65度等多种度数。</p>\r\n<div class=\"spctrl\">&nbsp;适时饮用，有活血驱寒，提神祛劳之益。</div>\r\n<p>&nbsp;西凤酒是中国最古老的历史名酒之一。凤翔古称雍州，地处古周原，是中华民族先祖的定居地区之一。这里又是上古农业大师后稷教民稼墙的地方，历来颇具兴农酿酒之地利，也是中国古代文化的中心地区之一。</p>\r\n<p><img src=\"/images/upload/Image/01(4).jpg\" width=\"675\" height=\"1856\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/baijiulie.jpg\" width=\"675\" height=\"2540\" alt=\"\" /></p>\r\n</div>', 'images/201303/thumb_img/444_thumb_G_1363028103140.jpg', 'images/201303/goods_img/444_G_1363028103583.jpg', 'images/201303/source_img/444_G_1363028103336.png', '1', '', '1', '1', '0', '0', '1363028103', '100', '0', '0', '1', '1', '0', '0', '1364270164', '1', '', '-1', '-1', '0', '');
