INSERT INTO `lg_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('469', '2', 'LG000469', '50° 古井贡酒 16年份 原浆酒 500ml ', '+', '31', '67', '', '1', '0.000', '0.00', '0.00', '0.00', '0', '0', '1', '', '', '<p>&nbsp;<strong><font size=\"4\">安徽古井贡酒股份有限公司简介</font></strong></p>\r\n<p>&nbsp;&nbsp;&nbsp;  安徽古井贡酒股份有限公司是中国老八大名酒企业，是国内第一家白酒类上市公司，坐落在历史名人曹操与华佗故里――安徽省亳州市。公司的前身为起源于明代正德十年（公元1515年）的公兴槽坊，1959年转制为省营亳县古井酒厂。在2009年中国500最具品牌价值排行榜中，&ldquo;古井贡&rdquo;以46.60亿元位居第175位，并获选&ldquo;影响世界的民族品牌&rdquo;。<br />\r\n&nbsp;&nbsp;&nbsp;  古井贡酒是公司主要产品，其渊源始于公元196年曹操将家乡亳州产的&ldquo;九酝春酒&rdquo;和酿造方法晋献给汉献帝刘协，自此一直作为皇室贡品。它以&ldquo;色清如水晶、香纯似幽兰、入口甘美醇和、回味经久不息&rdquo;的独特风格，四次蝉联全国白酒评比金奖，是巴黎第十三届国际食品博览会上唯一获金奖的中国名酒，先后获得中国驰名商标、中国原产地域保护产品、国家文物保护单位、国家非物质文化遗产保护项目等荣誉，被世人誉为&ldquo;酒中牡丹&rdquo;。<br />\r\n&nbsp;&nbsp;&nbsp;  目前公司主打产品古井贡酒&ldquo;年份原浆&rdquo;，以&ldquo;桃花曲、无极水、九酝酒法、明代窖池&rdquo;的优良品质被安徽省委省政府指定为接待专用酒，并获选2009年全国&ldquo;两会&rdquo;政协宴会用高档白酒，重返国宴。安徽古井贡酒股份有限公司多年来一直位列中国白酒企业前十强，2008年10月古井酒文化博览园被国家旅游局批准为AAAA级旅游景区，这也是中国白酒业第一家AAAA景区。</p>\r\n<p><img src=\"/images/upload/Image/baijiulie.jpg\" width=\"675\" height=\"2540\" alt=\"\" /></p>', 'images/201303/thumb_img/469_thumb_G_1363049956702.jpg', 'images/201303/goods_img/469_G_1363049956470.jpg', 'images/201303/source_img/469_G_1363049956167.png', '1', '', '1', '1', '0', '0', '1363049956', '100', '0', '1', '1', '1', '0', '0', '1363050009', '1', '', '-1', '-1', '0', '');
