INSERT INTO `lg_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('471', '2', 'LG000471', '54° 董酒 红色经典 500ml ', '+', '34', '68', '', '1', '0.000', '0.00', '0.00', '0.00', '0', '0', '1', '', '', '<p><img src=\"/images/upload/Image/2df6e45f999a4ad9b84920583d02649c_w(1).jpg\" width=\"675\" height=\"1185\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>【产品介绍】董酒有别于酱、浓、清、米香型酒，因舒适药香和爽口微酸而别具一格，为中国老八大名酒之一。根据国家白酒评酒委员会意见，将董酒列为  &ldquo;其它香型&rdquo;代表，2008年9月由国家主管部门正式确定为&ldquo;董香型&rdquo;标准体系。为保护这一传统名产，&ldquo;董酒工艺与酿造配方&rdquo;于1983年、1996年、  2006年先后连续三次被国家轻工业部、国家科学技术部、国家保密局列为科学技术保密项目&ldquo;国家秘密&rdquo;，严禁对外作泄密性宣传，保密期限为长期。  董酒，采用优质高梁为主要原料，以厂区西面八公里的水口寺地下泉水为酿造用水，小曲小窖制取酒醅，大曲大窖制取香醅，酒醅香醅串蒸精心酿造而成。其工艺简  称为&ldquo;两小两大、双醅串香&rdquo;，并在制曲过程中加入了130余味纯天然植物，这一独特的、精湛的酿造工艺造就董酒在白酒行业里典型的独特风格：既有大曲酒的  浓郁芳香，又有小曲酒的柔绵、醇和、回甜，还有微微的、淡雅舒适的药香和爽口的微酸，酒体丰满协调。专家们概括为：酒液清澈透明、香气幽雅舒适、入口醇和  浓郁、饮后甘爽味长。</p>\r\n<p><img src=\"/images/upload/Image/01(13).jpg\" width=\"675\" height=\"2981\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/baijiulie.jpg\" width=\"675\" height=\"2540\" alt=\"\" /></p>\r\n<p>&nbsp;</p>', 'images/201303/thumb_img/471_thumb_G_1363051097698.jpg', 'images/201303/goods_img/471_G_1363051097126.jpg', 'images/201303/source_img/471_G_1363051097520.png', '1', '', '1', '1', '0', '0', '1363051097', '100', '0', '0', '1', '1', '0', '0', '1363289327', '1', '', '-1', '-1', '0', '');
