INSERT INTO `lg_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('525', '41', 'LG000525', '青花瓷茶叶罐（花开富贵）', '+', '18', '0', '', '1', '0.000', '0.00', '0.00', '0.00', '0', '0', '1', '', '', '<p><span style=\"widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 12px/18px 宋体, Verdana, Arial; white-space: normal; orphans: 2; float: none; letter-spacing: normal; color: rgb(0,0,0); word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">&nbsp;青花瓷（blue and white porcelain），又称白地青花瓷，常简称青花，是中国瓷器的主流品种之一，属釉下彩瓷。青花瓷是用含氧化钴的钴矿为原料，在陶瓷坯体上描绘纹饰，再罩上一层透明釉，经高温还原焰一次烧成。钴料烧成后呈蓝色，具有着色力强、发色鲜艳、烧成率高、呈色稳定的特点。</span></p>\r\n<p><span style=\"widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 12px/18px 宋体, Verdana, Arial; white-space: normal; orphans: 2; float: none; letter-spacing: normal; color: rgb(0,0,0); word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><img width=\"675\" height=\"2033\" alt=\"\" src=\"/images/upload/Image/xiangqing(3).jpg\" /></span></p>\r\n<p><span style=\"widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 12px/18px 宋体, Verdana, Arial; white-space: normal; orphans: 2; float: none; letter-spacing: normal; color: rgb(0,0,0); word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">\r\n<div class=\"xx_font\" style=\"widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; font: 12px/18px 宋体, Verdana, Arial; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(0,0,0); word-break: break-all; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">青花瓷在中国古典文化中占有非常重要的地位,也深受国内外友人的喜爱,但是青花瓷具体表现了中国文化何种的魅力,却很少很有提及,本论文从青花瓷各个阶段时期的特点来阐述其的魅力之处,以期着重表达青花瓷在中国历史上的出现乃是浓重一笔。</div>\r\n<p style=\"padding-bottom: 0px; widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 12px/18px 宋体, Verdana, Arial; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(0,0,0); cursor: pointer; word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">&nbsp;陶瓷是人类的伟大发明，在漫长的历史长河中，我们的祖先以强烈的生命热望和极大的创造力，为我们留下了灿烂的陶瓷艺术精品，这些陶瓷艺术品记载了中华民族成长的历程，包容了中国人深沉婉约、融于自然，却又慷慨激昂、粗犷豪迈的情怀。从平民百姓到皇室贵族，从物质生活到意识形态，中国人与陶瓷艺术品有着千丝万缕的情愫。</p>\r\n<pre id=\"best-content-626243604\" class=\"best-text mb-10\" accuse=\"aContent\" style=\"line-height: 18px; widows: 2; text-transform: none; background-color: rgb(255,255,255); font-variant: normal; font-style: normal; text-indent: 0px; orphans: 2; letter-spacing: normal; color: rgb(0,0,0); font-size: 12px; font-weight: normal; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">\r\n   你也许也知道，英语中，大写&ldquo;China&rdquo;是中国，而小写&ldquo;china&rdquo;则指的则是陶瓷。中国人发明了瓷器这一事实举世公认，陶瓷\r\n与中国人有着特殊的联系，中国人对陶瓷也有着特殊的情感。陶瓷艺术品以自身独特的&ldquo;泥&rdquo;的精神，&ldquo;玉&rdquo;的品质而成为\r\n中华民族文明的见证。</pre>\r\n</span></p>', 'images/201303/thumb_img/525_thumb_G_1363816004490.jpg', 'images/201303/goods_img/525_G_1363816004759.jpg', 'images/201303/source_img/525_G_1363816004072.jpg', '1', '', '1', '1', '0', '0', '1363815984', '100', '0', '0', '1', '1', '0', '0', '1364011334', '5', '', '-1', '-1', '0', '');
