INSERT INTO `lg_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('500', '2', 'LG000500', '52°红星珍品二锅头酒500ml', '+', '35', '24', '', '1', '0.000', '0.00', '0.00', '0.00', '0', '0', '1', '', '', '<p>【商品介绍】<br />\r\n&nbsp;  红星二锅头酒是传统酿酒工艺与现代微生物技术相结合的结晶，以优质红高梁、大麦、碗豆、纯净水为原料，清蒸清烧，老五甑工艺蒸馏，掐头去尾截取中段，长年窖存。酒质清澈透明，气味香馥、醇厚甘冽、清香纯正、回味悠长。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<!--replace img-->&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<!--replace img-->&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<div style=\"text-align: left; \">&nbsp;<br />\r\n【品牌文化】<br />\r\n&nbsp;&nbsp;&nbsp;  红星最早传承北京八百年烧酒技艺：白酒酿造技术的记载最早产生在元代。明代李时珍在《本草纲目&middot;谷部》卷二十五中写道：&ldquo;烧酒，非古法也。自元时始创其法&rdquo;。有关专家证实，二锅头源于北京烧酒已有800年历史了。<br />\r\n&nbsp;&nbsp;&nbsp;  红星企业成立于1949年，是作为新中国的献礼而指定建设的项目之一。为了能让建国初期生活水平都普遍不高的中国大众都能喝上纯正的二锅头酒，国家规定红星二锅头酒的价格不得过高。所以，自红星问世五十多年以来，所生产十余种产品都属于低价位酒。由于红星二锅头甘烈醇厚，价位低廉，受到消费者始终不变的青睐，&ldquo;红星二锅头&rdquo;也成了&ldquo;大众的好酒&rdquo;的代名词。五十年来，红星品牌下的各种低价位产品始终保持着高销量，一直稳坐北京地区低端白酒市场的第一把交椅。<br />\r\n&nbsp;  &nbsp;&ldquo;北京红星&rdquo;为了迎接新中国诞生，于1949年8月生产第一批红星二锅头酒，9月投放市场，产品一上市便以其醇厚甘冽的品质，深受广大群众的喜好，被誉为&quot; 大众名酒  &quot;  。红星二锅头酒是京文化典型代表。红星是二锅头酒的创始人，从1949年的300吨发展到目前的6万吨，创造了全国清香型白酒产销量的新记录，满足了人民群众的生活要求。</div>\r\n<div style=\"text-align: left; \"><img src=\"/images/upload/Image/01(47).jpg\" width=\"675\" height=\"1856\" alt=\"\" /></div>\r\n<div style=\"text-align: left; \"><img src=\"/images/upload/Image/baijiulie.jpg\" width=\"675\" height=\"2540\" alt=\"\" /></div>', 'images/201303/thumb_img/500_thumb_G_1363286463149.jpg', 'images/201303/goods_img/500_G_1363286463982.jpg', 'images/201303/source_img/500_G_1363286463533.jpg', '1', '', '1', '1', '0', '0', '1363199848', '100', '0', '1', '0', '1', '0', '0', '1363917828', '1', '', '-1', '-1', '0', '');
