INSERT INTO `lg_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('528', '68', 'LG000528', '贵州茅台2010年上海世博会五十年陈酿收藏纪念珍藏名酒', '+', '32', '0', '', '1', '0.000', '0.00', '0.00', '0.00', '0', '0', '1', '', '', '<p style=\"padding-bottom: 0px; widows: 2; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 12px/18px 宋体, Verdana, Arial; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(0,0,0); cursor: pointer; word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">茅台世博喜酒简介：</p>\r\n<p style=\"padding-bottom: 0px; widows: 2; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 12px/18px 宋体, Verdana, Arial; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(0,0,0); cursor: pointer; word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2010年上海世博会纪念酒由上海世博会白酒行业唯一高级赞助商茅台集团分别用50年、30年、15年茅台陈酿等特制而成。1915年，中国贵州茅台首次与世界博览会结缘，在美国旧金山举行的巴拿马世博会上获金奖，一举成名。</p>\r\n<p style=\"padding-bottom: 0px; widows: 2; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 12px/18px 宋体, Verdana, Arial; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(0,0,0); cursor: pointer; word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 贵州茅台为本次世博会设计的81款特制纪念酒共分为四个系列：一是用50年茅台酒装瓶的世博中国馆限量纪念酒&mdash;&mdash;&ldquo;盛世中国世博珍藏酒&rdquo;，此酒以中国文化 元素为设计理念，用珍贵的黄花梨木刻为包装，寓意中华文化博大精深的历史传统；二是以30年茅台装瓶的个性化国家馆限量纪念酒&mdash;&mdash;&ldquo;和平使者&rdquo;，此酒针对 本次世博会45家参会国，按不同国家的文化元素，设计了45种不同的酒瓶包装。三是以15年茅台酒装瓶的个性化省区市馆限量纪念酒&mdash;&mdash;&ldquo;醉美中华&rdquo;，以各 省区市参会场馆图形为特色，设计了整套富有地方特色的包装方案；四是公开面市的纪念酒&mdash;&mdash;以特制茅台酒装瓶的世博喜酒。</p>\r\n<p style=\"padding-bottom: 0px; widows: 2; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 12px/18px 宋体, Verdana, Arial; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(0,0,0); cursor: pointer; word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">世博喜酒&rdquo;茅台53度世博特制喜酒，特选53度贵州茅台酒，具有酱香突出、幽雅细腻、酒体醇厚、回味悠长、空杯留香持久的独特风格。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;世博喜酒&rdquo;的包装设计以传统&ldquo;喜&rdquo;字组合吉祥图案，突出热烈祥和、仁和富足的庆典元素。以体现举家同喜，举国同庆的世博盛会的节日气氛。&ldquo;世博喜酒&rdquo;特制20万樽，是喜事与幸福时刻最好的见证，具有独特的收藏价值与意义。</p>\r\n<p style=\"padding-bottom: 0px; widows: 2; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 12px/18px 宋体, Verdana, Arial; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(0,0,0); cursor: pointer; word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"700\" align=\"center\" height=\"40\" style=\"widows: 2; text-transform: none; text-indent: 0px; border-collapse: collapse; font: 12px/18px 宋体, Verdana, Arial; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(0,0,0); word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">\r\n    <tbody>\r\n        <tr class=\"tr2\" style=\"text-align: center; line-height: 24px; background-color: rgb(255,255,255)\">\r\n            <td heig