INSERT INTO `lg_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('518', '41', 'LG000518', '一斤装青花瓷茶叶罐', '+', '44', '0', '', '1', '0.000', '0.00', '0.00', '0.00', '0', '0', '1', '', '', '<p>青花瓷（blue and white porcelain），又称白地青花瓷，常简称青花，是中国瓷器的主流品种之一，属釉下彩瓷。青花瓷是用含氧化钴的钴矿为原料，在陶瓷坯体上描绘纹饰，再罩上一层透明釉，经高温还原焰一次烧成。钴料烧成后呈蓝色，具有着色力强、发色鲜艳、烧成率高、呈色稳定的特点。</p>\r\n<p><img src=\"/images/upload/Image/1-zong.jpg\" width=\"675\" height=\"1391\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<div style=\"word-break: break-all; \" class=\"xx_font\">青花瓷在中国古典文化中占有非常重要的地位,也深受国内外友人的喜爱,但是青花瓷具体表现了中国文化何种的魅力,却很少很有提及,本论文从青花瓷各个阶段时期的特点来阐述其的魅力之处,以期着重表达青花瓷在中国历史上的出现乃是浓重一笔。</div>\r\n<p>&nbsp;陶瓷是人类的伟大发明，在漫长的历史长河中，我们的祖先以强烈的生命热望和极大的创造力，为我们留下了灿烂的陶瓷艺术精品，这些陶瓷艺术品记载了中华民族成长的历程，包容了中国人深沉婉约、融于自然，却又慷慨激昂、粗犷豪迈的情怀。从平民百姓到皇室贵族，从物质生活到意识形态，中国人与陶瓷艺术品有着千丝万缕的情愫。</p>\r\n<pre id=\"best-content-626243604\" class=\"best-text mb-10\" accuse=\"aContent\">\r\n   你也许也知道，英语中，大写&ldquo;China&rdquo;是中国，而小写&ldquo;china&rdquo;则指的则是陶瓷。中国人发明了瓷器这一事实举世公认，陶瓷\r\n与中国人有着特殊的联系，中国人对陶瓷也有着特殊的情感。陶瓷艺术品以自身独特的&ldquo;泥&rdquo;的精神，&ldquo;玉&rdquo;的品质而成为\r\n中华民族文明的见证。</pre>', 'images/201303/thumb_img/518_thumb_G_1363650651561.jpg', 'images/201303/goods_img/518_G_1363650651609.jpg', 'images/201303/source_img/518_G_1363650651915.jpg', '1', '', '1', '1', '0', '0', '1363630268', '100', '0', '1', '0', '1', '0', '0', '1364258788', '5', '', '-1', '-1', '0', '');
